  `invoice_id` INT UNSIGNED NOT NULL,
  `order_item_id` INT UNSIGNED NOT NULL,
  `qty_invoiced` INT NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`),
  KEY `orders_invoice_items_invoice_id` (`invoice_id`),
  KEY `orders_invoice_items_order_item_id` (`order_item_id`),
  CONSTRAINT `orders_invoice_items_invoice_id` FOREIGN KEY (`invoice_id`) REFERENCES `orders_invoices` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `orders_invoice_items_order_item_id` FOREIGN KEY (`order_item_id`) REFERENCES `orders_item` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB;

